import React, {useState} from 'react';
import './Task.css';

function Task(props) {
    const [editStatus, setEditStatus] = useState(false);
    console.log("in Task");

    function toggleEdit(){
        setEditStatus(!editStatus);
    }
    return <div key={props.id}>
        <div id="task" className={(props.completed) ? "completed" : "incomplete"}>
            <input type="checkbox" className="check" onClick={props.toggleComplete }/>
            <input type="text" className={(editStatus) ? 'writingEdit':'writingSaved'} >{props.taskText}</input>
        </div>
        <div className="editButton"><button onClick={e => toggleEdit()}>Edit</button></div>
        <div className={'fullButton'}>
            <button className={"deleteButton"} onClick={e => props.onDeleteTask(props.elemid)}>Delete</button>
        </div>
    </div>
}

export default Task;
